'use client';

import { useEffect, useState } from 'react';
import { Table, Badge, Image } from 'antd';
import config from '../config';

function DeviceDashboard() {
  const [devices, setDevices] = useState<any[]>([]);

  const fetchDevices = async () => {
    try {
      const response = await fetch('/api/devices');
      const data = await response.json();
      setDevices(Array.isArray(data) ? data : []);
    } catch (error) {
      console.error('Error fetching devices:', error);
      setDevices([]);
    }
  };

  useEffect(() => {
    fetchDevices();

    const interval = setInterval(() => {
      fetchDevices();
    }, config.heartbeatIntervalMs);

    return () => clearInterval(interval);
  }, []);

  const columns = [
    {
      title: 'Screen',
      dataIndex: ['lastHeartbeatDetails', 'screenImagePath'],
      key: 'screenImagePath',
      render: (screenImagePath: string) =>
        screenImagePath ? (
          <Image
            height={150}
            src={`${screenImagePath}?t=${new Date().getTime()}`}
            fallback='data:image/png;base64,'
          />
        ) : (
          <Image
            height={150}
            src='error'
            fallback='data:image/png;base64,'
          />
        ),
    },
    {
      title: 'Status',
      dataIndex: 'status',
      key: 'status',
      render: (status: string) =>
        status == 'online' ? (
          <Badge status='success' text='Online' />
        ) : (
          <Badge status='error' text='Offline' />
        ),
    },
    {
      title: 'Name',
      dataIndex: 'nickname',
      key: 'nickname',
    },
    {
      title: 'Key',
      dataIndex: 'deviceKey',
      key: 'deviceKey',
    },
    {
      title: 'Last timestamp',
      dataIndex: ['lastHeartbeatDetails', 'timestamp'],
      key: 'timestamp',
      render: (timestamp: string) => {
        if (!timestamp) return 'N/A';

        const kstDate = new Date(timestamp);

        const year = kstDate.getFullYear();
        const month = kstDate.getMonth() + 1;
        const day = kstDate.getDate();
        const hours = kstDate.getHours();
        const minutes = kstDate.getMinutes();
        const seconds = kstDate.getSeconds();

        const now = new Date();
        const diffMs = now.getTime() - kstDate.getTime();
        const diffHours = Math.floor(diffMs / (1000 * 60 * 60));
        const diffMinutes = Math.floor(
          (diffMs % (1000 * 60 * 60)) / (1000 * 60)
        );

        const timeAgo =
          diffHours === 0 && diffMinutes === 0
            ? '조금 전'
            : `${diffHours}시간 ${diffMinutes}분 전`;

        return `${year}년 ${month}월 ${day}일 ${hours}시 ${minutes}분 ${seconds}초 (${timeAgo})`;
      },
    },
    {
      title: 'Tilt',
      dataIndex: ['lastHeartbeatDetails', 'tilt'],
      key: 'tilt',
      render: (text: string, record: any) =>
        record.status === 'offline' ? 'N/A' : text || 'N/A',
    },
    {
      title: 'Battery',
      dataIndex: ['lastHeartbeatDetails', 'batteryPercentage'],
      key: 'batteryPercentage',
      render: (text: string, record: any) =>
        record.status === 'offline' ? 'N/A' : text ? `${text}%` : 'N/A',
    },
    {
      title: 'Charging',
      dataIndex: ['lastHeartbeatDetails', 'isCharging'],
      key: 'isCharging',
      render: (isCharging: boolean, record: any) =>
        record.status === 'offline' ? (
          'N/A'
        ) : isCharging ? (
          <Badge status='success' text='Yes' />
        ) : (
          <Badge status='error' text='No' />
        ),
    },
  ];

  return (
    <div>
      <Table
        dataSource={devices}
        columns={columns}
        rowKey='deviceKey'
        pagination={{ pageSize: 10 }}
        rowClassName={(record) =>
          record.status === 'offline' ? 'offline-row' : ''
        }
      />
    </div>
  );
}

export default DeviceDashboard;
